mapper.update:
UPDATE
		TB_MB
SET
		ALS = #{als}
		,EMAIL = #{email}
		,DOMAIN = #{domain}
		,ADDR1 = #{addr1}
		,ADDR2 = #{addr2}
		,PHON1 = #{phon1}
		,PHON2 =#{phon2}
		,PHON3 = #{phon3}
		,TEL1 = #{tel1}
		,TEL2 = #{tel2}
		,TEL3 = #{tel3}
WHERE
		PW = #{pw}

UPDATE
		TB_MB
SET
		PW = #{newPw}
WHERE
		ID = #{id}
AND
		PW = #{pw}

mapper.select:
SELECT
		ID
FROM
		TB_MB
WHERE
		NM = #{nm}
AND
		EMAIL = #{email}
AND
		DOMAIN = #{domain}

SELECT
		PW
FROM
		TB_MB
WHERE
		ID = #{id}
AND
		NM = #{nm}
AND
		EMAIL = #{email}
AND
		DOMAIN = #{domain}
