mapper.insert:
INSERT INTO
		TB_REPLY
		(
		RPL_NUM
		,BOARD_NUM
		,ID
		,RPL_CN
		,CRT_TM
		,UP_TM
		,DEPTH
		,PW
		,PARENT_RPL
		)
VALUES
		(
		TB_REPLY_SEQ.NEXTVAL
		,#{boardNum}
		,#{id}
		,#{rplCn}
		,SYSDATE
		,SYSDATE
		,#{depth}
		,#{pw}
		,#{parentRpl}
		)

mapper.update:
UPDATE
		TB_REPLY
SET
		RPL_CN = #{rplCn}
		,UP_TM = SYSDATE
WHERE
		ID = #{id}
AND
		PW = #{pw}
AND
		RPL_NUM = #{rplNum}

UPDATE
		TB_REPLY
SET
		RPL_CN = #{rplCn}
		,UP_TM = SYSDATE
WHERE
		ID = #{id}
AND
		RPL_NUM = #{rplNum}

mapper.delete:
DELETE
FROM
		TB_REPLY
WHERE
		ID = #{id}
AND
		PW = #{pw}
AND
		RPL_NUM = #{rplNum}
